import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  imports: [],
  templateUrl: './home.component.html',
  styleUrl: './home.component.css'
})
export class HomeComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Oracle - Home</title>
  <link rel="stylesheet" href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap">
  <style>
    * {
      margin: 0;
      padding: 0;
      box-sizing: border-box;
      font-family: 'Inter', sans-serif;
      scroll-behavior: smooth;
    }

    :root {
      --primary: #6366f1;
      --primary-dark: #4f46e5;
      --primary-light: #818cf8;
      --primary-bg: #eef2ff;
      --success: #10b981;
      --error: #ef4444;
      --text-dark: #1f2937;
      --text-medium: #4b5563;
      --text-light: #6b7280;
      --border: #e5e7eb;
      --border-focus: #a5b4fc;
      --bg-light: #f9fafb;
      --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
      --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
      --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
      --yes-color: #10b981;
      --no-color: #ef4444;
    }

    body {
      background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('/public/image.jpg');
      background-size: cover;
      background-position: center;
      background-attachment: fixed;
      color: var(--text-dark);
      line-height: 1.6;
      min-height: 100vh;
      display: flex;
      flex-direction: column;
    }


    /* Hero Section */
    .hero-section {
      padding: 3rem 5%;
      max-width: 1400px;
      margin: 0 auto;
      width: 100%;
    }

    .home-content {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 3rem;
      align-items: start;
    }

    .welcome-section {
      color: white;
      padding-top: 2rem;
    }

    .welcome-section h1 {
      font-size: 2.5rem;
      font-weight: 800;
      margin-bottom: 1.5rem;
      line-height: 1.2;
    }

    .welcome-section p {
      font-size: 1.1rem;
      margin-bottom: 2rem;
      opacity: 0.9;
      max-width: 90%;
    }

    .feature-list {
      list-style: none;
      margin-bottom: 2rem;
    }

    .feature-list li {
      display: flex;
      align-items: center;
      gap: 0.75rem;
      margin-bottom: 1rem;
      font-size: 1.05rem;
    }

    .feature-icon {
      width: 24px;
      height: 24px;
      fill: var(--primary-light);
      flex-shrink: 0;
    }

    .cta-btn {
      display: inline-block;
      background: linear-gradient(90deg, var(--primary) 0%, var(--primary-dark) 100%);
      color: white;
      padding: 0.75rem 1.5rem;
      border-radius: 8px;
      font-weight: 600;
      text-decoration: none;
      transition: all 0.3s;
      box-shadow: 0 4px 12px rgba(79, 70, 229, 0.2);
    }

    .cta-btn:hover {
      transform: translateY(-2px);
      box-shadow: 0 6px 16px rgba(79, 70, 229, 0.3);
    }

    /* Oracle Form */
    .form-container {
      background-color: rgba(255, 255, 255, 0.95);
      backdrop-filter: blur(10px);
      -webkit-backdrop-filter: blur(10px);
      border-radius: 16px;
      box-shadow: var(--shadow-lg);
      padding: 2rem;
      position: relative;
      overflow: hidden;
      animation: fadeIn 0.5s ease-out;
    }

    .form-container::before {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 6px;
      background: linear-gradient(90deg, var(--primary) 0%, var(--primary-light) 100%);
      z-index: 1;
    }

    .form-header {
      margin-bottom: 1.5rem;
    }

    .form-header h2 {
      font-size: 1.5rem;
      font-weight: 700;
      margin-bottom: 0.5rem;
      color: var(--text-dark);
    }

    .form-header p {
      color: var(--text-light);
      font-size: 0.95rem;
    }

    .oracle-form {
      display: flex;
      flex-direction: column;
      gap: 1.25rem;
    }

    .form-group {
      display: flex;
      flex-direction: column;
      gap: 0.5rem;
    }

    .form-group label {
      font-size: 0.9rem;
      font-weight: 600;
      color: var(--text-medium);
    }

    .form-group input,
    .form-group textarea {
      padding: 0.75rem 1rem;
      border: 2px solid var(--border);
      border-radius: 8px;
      font-size: 1rem;
      transition: all 0.2s ease;
      background-color: var(--bg-light);
    }

    .form-group textarea {
      min-height: 120px;
      resize: none;
    }

    .form-group input:hover,
    .form-group textarea:hover {
      border-color: var(--border-focus);
    }

    .form-group input:focus,
    .form-group textarea:focus {
      outline: none;
      border-color: var(--primary);
      box-shadow: 0 0 0 4px rgba(99, 102, 241, 0.1);
      background-color: white;
    }

    .submit-btn {
      background: linear-gradient(90deg, var(--primary) 0%, var(--primary-dark) 100%);
      color: white;
      border: none;
      border-radius: 8px;
      padding: 0.75rem 1rem;
      font-size: 1rem;
      font-weight: 600;
      cursor: pointer;
      transition: all 0.3s ease;
      box-shadow: 0 4px 12px rgba(79, 70, 229, 0.2);
      position: relative;
      overflow: hidden;
    }

    .submit-btn:hover {
      transform: translateY(-2px);
      box-shadow: 0 6px 16px rgba(79, 70, 229, 0.3);
    }

    .submit-btn:active {
      transform: translateY(0);
      box-shadow: 0 2px 8px rgba(79, 70, 229, 0.2);
    }

    .submit-btn::after {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
      transform: translateX(-100%);
    }

    .submit-btn:hover::after {
      animation: shine 1.5s infinite;
    }

    @keyframes shine {
      100% {
        transform: translateX(100%);
      }
    }

    .loading {
      display: none;
      text-align: center;
      margin: 1rem 0;
    }

    .loading-dots {
      display: inline-block;
    }

    .loading-dots span {
      display: inline-block;
      width: 8px;
      height: 8px;
      border-radius: 50%;
      background-color: var(--primary);
      margin: 0 3px;
      animation: loading 1.4s infinite ease-in-out both;
    }

    .loading-dots span:nth-child(1) {
      animation-delay: -0.32s;
    }

    .loading-dots span:nth-child(2) {
      animation-delay: -0.16s;
    }

    @keyframes loading {
      0%, 80%, 100% {
        transform: scale(0);
      }
      40% {
        transform: scale(1.0);
      }
    }

    .result-container {
      margin-top: 1.5rem;
      padding: 1.25rem;
      border-radius: 12px;
      background-color: white;
      box-shadow: var(--shadow-md);
      display: none;
      animation: fadeIn 0.5s ease-out;
    }

    .result-header {
      display: flex;
      align-items: center;
      justify-content: space-between;
      margin-bottom: 1rem;
    }

    .result-title {
      font-size: 1.1rem;
      font-weight: 600;
      color: var(--text-dark);
    }

    .result-answer {
      font-size: 0.9rem;
      font-weight: 700;
      padding: 0.25rem 1rem;
      border-radius: 20px;
      color: white;
    }

    .result-answer.yes {
      background-color: var(--yes-color);
    }

    .result-answer.no {
      background-color: var(--no-color);
    }

    .result-quote {
      font-style: italic;
      color: var(--text-medium);
      margin-bottom: 1rem;
      padding-left: 1rem;
      border-left: 3px solid var(--primary-light);
      font-size: 0.95rem;
    }

    .result-reason {
      color: var(--text-medium);
      line-height: 1.6;
      font-size: 0.95rem;
    }

    .result-speaker {
      font-weight: 600;
      color: var(--primary-dark);
    }

    /* Content Sections */
    .content-section {
      padding: 5rem 5%;
      max-width: 1400px;
      margin: 0 auto;
      width: 100%;
    }

    .section-light {
      background-color: rgba(255, 255, 255, 0.95);
      color: var(--text-dark);
    }

    .section-dark {
      background-color: rgba(31, 41, 55, 0.9);
      color: white;
    }

    .section-header {
      text-align: center;
      margin-bottom: 3rem;
    }

    .section-header h2 {
      font-size: 2.2rem;
      font-weight: 700;
      margin-bottom: 1rem;
      position: relative;
      display: inline-block;
    }

    .section-header h2::after {
      content: "";
      position: absolute;
      bottom: -10px;
      left: 50%;
      transform: translateX(-50%);
      width: 80px;
      height: 4px;
      background: linear-gradient(90deg, var(--primary) 0%, var(--primary-light) 100%);
      border-radius: 2px;
    }

    .section-header p {
      font-size: 1.1rem;
      max-width: 700px;
      margin: 0 auto;
      color: inherit;
      opacity: 0.9;
    }

    /* About Section */
    .about-content {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 3rem;
      align-items: center;
    }

    .about-image {
      border-radius: 16px;
      overflow: hidden;
      box-shadow: var(--shadow-lg);
      position: relative;
    }

    .about-image img {
      width: 100%;
      height: auto;
      display: block;
    }

    .about-image::before {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: linear-gradient(135deg, rgba(99, 102, 241, 0.2) 0%, rgba(79, 70, 229, 0.2) 100%);
      z-index: 1;
    }

    .about-text h3 {
      font-size: 1.8rem;
      margin-bottom: 1.5rem;
      color: var(--text-dark);
    }

    .about-text p {
      margin-bottom: 1.5rem;
      font-size: 1.05rem;
      color: var(--text-medium);
    }

    /* Features Section */
    .features-grid {
      display: grid;
      grid-template-columns: repeat(3, 1fr);
      gap: 2rem;
    }

    .feature-card {
      background-color: white;
      border-radius: 12px;
      padding: 2rem;
      box-shadow: var(--shadow-md);
      transition: transform 0.3s ease, box-shadow 0.3s ease;
    }

    .feature-card:hover {
      transform: translateY(-5px);
      box-shadow: var(--shadow-lg);
    }

    .feature-icon-large {
      width: 50px;
      height: 50px;
      margin-bottom: 1.5rem;
      color: var(--primary);
    }

    .feature-card h3 {
      font-size: 1.3rem;
      margin-bottom: 1rem;
      color: var(--text-dark);
    }

    .feature-card p {
      color: var(--text-medium);
      font-size: 0.95rem;
    }

    /* Contact Section */
    .contact-content {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 3rem;
    }

    .contact-info {
      color: white;
    }

    .contact-info h3 {
      font-size: 1.8rem;
      margin-bottom: 1.5rem;
    }

    .contact-info p {
      margin-bottom: 2rem;
      font-size: 1.05rem;
      opacity: 0.9;
    }

    .contact-methods {
      list-style: none;
    }

    .contact-methods li {
      display: flex;
      align-items: center;
      gap: 1rem;
      margin-bottom: 1.5rem;
    }

    .contact-icon {
      width: 24px;
      height: 24px;
      color: var(--primary-light);
    }

    .contact-form {
      background-color: white;
      border-radius: 16px;
      padding: 2rem;
      box-shadow: var(--shadow-lg);
    }

    .contact-form h3 {
      font-size: 1.5rem;
      margin-bottom: 1.5rem;
      color: var(--text-dark);
    }

    .contact-form .form-group {
      margin-bottom: 1.25rem;
    }

    /* Footer */
    footer {
      background-color: rgba(17, 24, 39, 0.95);
      backdrop-filter: blur(10px);
      -webkit-backdrop-filter: blur(10px);
      color: white;
      padding: 2rem 5%;
      margin-top: auto;
    }

    .footer-content {
      max-width: 1400px;
      margin: 0 auto;
      display: flex;
      justify-content: space-between;
      align-items: center;
    }

    .footer-logo {
      font-weight: 700;
      font-size: 1.2rem;
      color: white;
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .footer-links {
      display: flex;
      gap: 1.5rem;
    }

    .footer-links a {
      color: rgba(255, 255, 255, 0.8);
      text-decoration: none;
      font-size: 0.9rem;
      transition: color 0.2s;
    }

    .footer-links a:hover {
      color: white;
    }

    .copyright {
      color: rgba(255, 255, 255, 0.6);
      font-size: 0.85rem;
    }

    /* Responsive Design */
    @media (max-width: 1024px) {
      .home-content,
      .about-content,
      .features-grid,
      .contact-content {
        gap: 2rem;
      }

      .welcome-section h1,
      .section-header h2 {
        font-size: 2rem;
      }

      .features-grid {
        grid-template-columns: repeat(2, 1fr);
      }
    }

    @media (max-width: 768px) {
      .home-content,
      .about-content,
      .contact-content {
        grid-template-columns: 1fr;
      }

      .welcome-section {
        order: 1;
      }

      .form-container {
        order: 2;
      }

      .nav-links {
        display: none;
        position: absolute;
        top: 100%;
        left: 0;
        right: 0;
        flex-direction: column;
        background-color: rgba(255, 255, 255, 0.95);
        padding: 1rem;
        box-shadow: var(--shadow-md);
        z-index: 100;
        text-align: center;
      }

      .nav-links.active {
        display: flex;
      }

      .mobile-menu-btn {
        display: block;
      }

      .footer-content {
        flex-direction: column;
        gap: 1rem;
        text-align: center;
      }

      .footer-links {
        margin: 1rem 0;
      }

      .features-grid {
        grid-template-columns: 1fr;
      }
    }

    @media (max-width: 480px) {
      .navbar {
        padding: 1rem;
