      .section-header h2 {
        font-size: 1.8rem;
      }

      .welcome-section p,
      .section-header p {
        font-size: 1rem;
      }

      .form-container,
      .feature-card,
      .contact-form {
        padding: 1.5rem;
      }

      .content-section {
        padding: 3rem 5%;
      }
    }

    /* Animation */
    @keyframes fadeIn {
      from {
        opacity: 0;
        transform: translateY(10px);
      }
      to {
        opacity: 1;
        transform: translateY(0);
      }
