<script>
  document.addEventListener('DOMContentLoaded', function() {
    const oracleForm = document.getElementById('oracleForm');
    const loading = document.getElementById('loading');
    const resultContainer = document.getElementById('resultContainer');
    const resultAnswer = document.getElementById('resultAnswer');
    const resultQuote = document.getElementById('resultQuote');
    const resultReason = document.getElementById('resultReason');

    // Mobile menu toggle
    const mobileMenuBtn = document.querySelector('.mobile-menu-btn');
    const navLinks = document.querySelector('.nav-links');

    mobileMenuBtn.addEventListener('click', function() {
      navLinks.classList.toggle('active');
    });

    // Smooth scrolling for navigation links
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
      anchor.addEventListener('click', function(e) {
        e.preventDefault();

        const targetId = this.getAttribute('href');
        const targetElement = document.querySelector(targetId);

        if (targetElement) {
          // Close mobile menu if open
          navLinks.classList.remove('active');

          // Scroll to the target element
          window.scrollTo({
            top: targetElement.offsetTop,
            behavior: 'smooth'
          });

          // Update active link
          document.querySelectorAll('.nav-links a').forEach(link => {
            link.classList.remove('active');
          });
          this.classList.add('active');
        }
      });
    });

    // Highlight active nav item on scroll
    window.addEventListener('scroll', function() {
      const scrollPosition = window.scrollY;

      document.querySelectorAll('section[id]').forEach(section => {
        const sectionTop = section.offsetTop - 100;
        const sectionHeight = section.offsetHeight;
        const sectionId = section.getAttribute('id');

        if (sectionTop <= scrollPosition && sectionTop + sectionHeight > scrollPosition) {
          document.querySelectorAll('.nav-links a').forEach(link => {
            link.classList.remove('active');
            if (link.getAttribute('href') === '#' + sectionId) {
              link.classList.add('active');
            }
          });
        }
      });
    });

    // Oracle form submission
    oracleForm.addEventListener('submit', function(e) {
      e.preventDefault();

      const speaker = document.getElementById('speaker').value;
      const question = document.getElementById('question').value;

      // Show loading
      loading.style.display = 'block';
      resultContainer.style.display = 'none';

      // Simulate API call with timeout
      setTimeout(() => {
        generateAnswer(speaker, question);
        loading.style.display = 'none';
        resultContainer.style.display = 'block';

        // Save to history (in a real app, this would be stored in a database)
        saveToHistory(speaker, question, resultAnswer.textContent, resultReason.textContent);
      }, 1500);
    });

    function generateAnswer(speaker, question) {
      // Randomly determine yes or no
      const isYes = Math.random() > 0.5;

      // Set the answer
      resultAnswer.textContent = isYes ? 'YES' : 'NO';
      resultAnswer.className = isYes ? 'result-answer yes' : 'result-answer no';

      // Set the quote
      resultQuote.innerHTML = `"${question}" - <span class="result-speaker">${speaker}</span>`;

      // Generate a reason based on yes/no
      const reasons = isYes ? [
        `According to our analysis, ${speaker}'s question has a positive outcome. The evidence suggests that the answer is affirmative because the underlying factors align favorably.`,
        `The oracle has consulted the cosmic forces and determined that ${speaker}'s inquiry deserves a positive response. The alignment of circumstances supports this conclusion.`,
        `Based on the patterns observed, ${speaker}'s question receives a positive answer. The trajectory of events indicates an affirmative outcome is most likely.`
      ] : [
        `After careful consideration, the oracle has determined that ${speaker}'s question must be answered negatively. The current circumstances do not support a positive outcome.`,
        `The cosmic forces have been consulted, and unfortunately for ${speaker}, the answer is negative. The alignment of factors suggests this is the most accurate response.`,
        `The oracle has analyzed the question posed by ${speaker} and must provide a negative answer. The evidence and patterns observed point to this conclusion.`
      ];

      // Select a random reason
      const randomReason = reasons[Math.floor(Math.random() * reasons.length)];
      resultReason.textContent = randomReason;
    }

    function saveToHistory(speaker, question, answer, reason) {
      // In a real application, this would save to localStorage, a database, or an API
      // For this demo, we'll just log to console
      console.log('Saved to history:', {
        timestamp: new Date().toISOString(),
        speaker,
        question,
        answer,
        reason
      });

      // This is where you would implement actual history storage
      // localStorage.setItem('oracleHistory', JSON.stringify([...history, newEntry]));
    }

    // Login and history button click events
    document.querySelector('.login-btn').addEventListener('click', function() {
      window.location.href = 'login.html';
    });

    document.querySelector('.history-btn').addEventListener('click', function() {
      window.location.href = 'history.html';
    });
  });
</script>
</body>
</html>
