import { Component } from '@angular/core';

@Component({
  selector: 'app-history',
  imports: [],
  templateUrl: './history.component.html',
  styleUrl: './history.component.css'
})
export class HistoryComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Oracle - History</title>
  <link rel="stylesheet" href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap">
  <style>
    * {
      margin: 0;
      padding: 0;
      box-sizing: border-box;
      font-family: 'Inter', sans-serif;
      scroll-behavior: smooth;
    }

    :root {
      --primary: #6366f1;
      --primary-dark: #4f46e5;
      --primary-light: #818cf8;
      --primary-bg: #eef2ff;
      --success: #10b981;
      --error: #ef4444;
      --text-dark: #1f2937;
      --text-medium: #4b5563;
      --text-light: #6b7280;
      --border: #e5e7eb;
      --border-focus: #a5b4fc;
      --bg-light: #f9fafb;
      --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
      --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
      --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
      --yes-color: #10b981;
      --no-color: #ef4444;
    }

    body {
      background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('image.jpg');
      background-size: cover;
      background-position: center;
      background-attachment: fixed;
      color: var(--text-dark);
      line-height: 1.6;
      min-height: 100vh;
      display: flex;
      flex-direction: column;
    }

    /* Header and Navigation */
    header {
      background-color: rgba(255, 255, 255, 0.95);
      backdrop-filter: blur(10px);
      -webkit-backdrop-filter: blur(10px);
      box-shadow: var(--shadow-md);
      position: sticky;
      top: 0;
      z-index: 100;
    }

    .navbar {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 1rem 5%;
      max-width: 1400px;
      margin: 0 auto;
    }

    .logo {
      font-size: 1.5rem;
      font-weight: 700;
      color: var(--primary-dark);
      text-decoration: none;
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .logo-icon {
      width: 28px;
      height: 28px;
      fill: var(--primary);
    }

    .nav-links {
      display: flex;
      gap: 2rem;
      list-style: none;
    }

    .nav-links a {
      color: var(--text-medium);
      text-decoration: none;
      font-weight: 500;
      transition: color 0.2s;
      font-size: 0.95rem;
      cursor: pointer;
    }

    .nav-links a:hover {
      color: var(--primary);
    }

    .nav-links a.active {
      color: var(--primary-dark);
      font-weight: 600;
    }

    .nav-buttons {
      display: flex;
      gap: 1rem;
    }

    .nav-btn {
      padding: 0.5rem 1rem;
      border-radius: 8px;
      font-weight: 600;
      font-size: 0.9rem;
      cursor: pointer;
      transition: all 0.2s;
      display: flex;
      align-items: center;
      gap: 0.5rem;
      text-decoration: none;
    }

    .login-btn {
      background-color: white;
      color: var(--primary-dark);
      border: 2px solid var(--primary-light);
    }

    .login-btn:hover {
      background-color: var(--primary-bg);
    }

    .history-btn {
      background-color: var(--primary);
      color: white;
      border: none;
      box-shadow: var(--shadow-sm);
    }

    .history-btn:hover {
      background-color: var(--primary-dark);
    }

    .mobile-menu-btn {
      display: none;
      background: none;
      border: none;
      font-size: 1.5rem;
      color: var(--text-medium);
      cursor: pointer;
    }

    /* Main Content */
    .main-content {
      flex: 1;
      padding: 3rem 5%;
      max-width: 1400px;
      margin: 0 auto;
      width: 100%;
    }

    .page-header {
      text-align: center;
      margin-bottom: 3rem;
      color: white;
    }

    .page-header h1 {
      font-size: 2.5rem;
      font-weight: 800;
      margin-bottom: 1rem;
    }

    .page-header p {
      font-size: 1.1rem;
      opacity: 0.9;
      max-width: 700px;
      margin: 0 auto;
    }

    /* History Controls */
    .history-controls {
      background-color: rgba(255, 255, 255, 0.95);
      backdrop-filter: blur(10px);
      -webkit-backdrop-filter: blur(10px);
      border-radius: 12px;
      padding: 1.5rem;
      margin-bottom: 2rem;
      display: flex;
      flex-wrap: wrap;
      gap: 1rem;
      justify-content: space-between;
      align-items: center;
    }

    .search-box {
      flex: 1;
      min-width: 250px;
      position: relative;
    }

    .search-box input {
      width: 100%;
      padding: 0.75rem 1rem 0.75rem 2.5rem;
      border: 2px solid var(--border);
      border-radius: 8px;
      font-size: 1rem;
      transition: all 0.2s ease;
    }

    .search-box input:focus {
      outline: none;
      border-color: var(--primary);
      box-shadow: 0 0 0 4px rgba(99, 102, 241, 0.1);
    }

    .search-icon {
      position: absolute;
      left: 0.75rem;
      top: 50%;
      transform: translateY(-50%);
      width: 20px;
      height: 20px;
      color: var(--text-light);
    }

    .filter-controls {
      display: flex;
      gap: 1rem;
      flex-wrap: wrap;
    }

    .filter-select {
      padding: 0.75rem 1rem;
      border: 2px solid var(--border);
      border-radius: 8px;
      font-size: 0.95rem;
      background-color: white;
      min-width: 150px;
    }

    .filter-select:focus {
      outline: none;
      border-color: var(--primary);
      box-shadow: 0 0 0 4px rgba(99, 102, 241, 0.1);
    }

    /* History List */
    .history-list {
      display: grid;
      grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
      gap: 1.5rem;
    }

    .history-card {
      background-color: rgba(255, 255, 255, 0.95);
      backdrop-filter: blur(5px);
      -webkit-backdrop-filter: blur(5px);
      border-radius: 12px;
      overflow: hidden;
      box-shadow: var(--shadow-md);
      transition: transform 0.3s ease, box-shadow 0.3s ease;
    }

    .history-card:hover {
      transform: translateY(-5px);
      box-shadow: var(--shadow-lg);
    }

    .card-header {
      padding: 1.25rem;
      border-bottom: 1px solid var(--border);
      display: flex;
      justify-content: space-between;
      align-items: center;
    }

    .card-date {
      font-size: 0.85rem;
      color: var(--text-light);
    }

    .card-answer {
      font-size: 0.85rem;
      font-weight: 700;
      padding: 0.25rem 1rem;
      border-radius: 20px;
      color: white;
    }

    .card-answer.yes {
      background-color: var(--yes-color);
    }

    .card-answer.no {
      background-color: var(--no-color);
    }

    .card-body {
      padding: 1.25rem;
    }

    .card-speaker {
      font-weight: 600;
      color: var(--primary-dark);
      margin-bottom: 0.5rem;
    }

    .card-question {
      font-size: 1.1rem;
      font-weight: 600;
      margin-bottom: 1rem;
      color: var(--text-dark);
    }

    .card-reason {
      color: var(--text-medium);
      font-size: 0.95rem;
      margin-bottom: 1rem;
    }

    .card-actions {
      display: flex;
      justify-content: flex-end;
      gap: 0.75rem;
    }

    .card-btn {
      padding: 0.5rem;
      border-radius: 8px;
      background: none;
      border: none;
      cursor: pointer;
      color: var(--text-medium);
      transition: all 0.2s;
    }

    .card-btn:hover {
      color: var(--primary);
      background-color: var(--primary-bg);
    }

    .card-btn svg {
      width: 18px;
      height: 18px;
    }

    /* Pagination */
    .pagination {
      display: flex;
      justify-content: center;
      margin-top: 3rem;
      gap: 0.5rem;
    }

    .page-btn {
      width: 40px;
      height: 40px;
      display: flex;
      align-items: center;
      justify-content: center;
      border-radius: 8px;
      background-color: white;
      border: 2px solid var(--border);
      font-weight: 600;
      cursor: pointer;
      transition: all 0.2s;
    }

    .page-btn:hover {
      border-color: var(--primary);
      color: var(--primary);
    }

    .page-btn.active {
      background-color: var(--primary);
      border-color: var(--primary);
      color: white;
    }

    .page-btn.disabled {
      opacity: 0.5;
      cursor: not-allowed;
    }

    .page-btn svg {
      width: 18px;
      height: 18px;
    }

    /* Empty State */
    .empty-state {
      text-align: center;
      padding: 4rem 2rem;
      background-color: rgba(255, 255, 255, 0.95);
      border-radius: 12px;
      margin-bottom: 2rem;
    }

    .empty-icon {
      width: 80px;
      height: 80px;
      color: var(--text-light);
      margin: 0 auto 1.5rem;
    }

    .empty-state h3 {
      font-size: 1.5rem;
      margin-bottom: 1rem;
      color: var(--text-dark);
    }

    .empty-state p {
      color: var(--text-medium);
      margin-bottom: 1.5rem;
      max-width: 500px;
      margin-left: auto;
      margin-right: auto;
    }

    .empty-btn {
      display: inline-block;
      background: linear-gradient(90deg, var(--primary) 0%, var(--primary-dark) 100%);
      color: white;
      padding: 0.75rem 1.5rem;
      border-radius: 8px;
      font-weight: 600;
      text-decoration: none;
      transition: all 0.3s;
      box-shadow: 0 4px 12px rgba(79, 70, 229, 0.2);
    }

    .empty-btn:hover {
      transform: translateY(-2px);
      box-shadow: 0 6px 16px rgba(79, 70, 229, 0.3);
    }

    /* Footer */
    footer {
      background-color: rgba(17, 24, 39, 0.95);
      backdrop-filter: blur(10px);
      -webkit-backdrop-filter: blur(10px);
      color: white;
      padding: 2rem 5%;
      margin-top: auto;
    }

    .footer-content {
      max-width: 1400px;
      margin: 0 auto;
      display: flex;
      justify-content: space-between;
      align-items: center;
    }

    .footer-logo {
      font-weight: 700;
      font-size: 1.2rem;
      color: white;
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .footer-links {
      display: flex;
      gap: 1.5rem;
    }

    .footer-links a {
      color: rgba(255, 255, 255, 0.8);
      text-decoration: none;
      font-size: 0.9rem;
      transition: color 0.2s;
    }

    .footer-links a:hover {
      color: white;
    }

    .copyright {
      color: rgba(255, 255, 255, 0.6);
      font-size: 0.85rem;
    }

    /* Responsive Design */
    @media (max-width: 1024px) {
      .history-list {
        grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
      }
    }

    @media (max-width: 768px) {
      .nav-links {
        display: none;
        position: absolute;
        top: 100%;
        left: 0;
        right: 0;
        flex-direction: column;
        background-color: rgba(255, 255, 255, 0.95);
        padding: 1rem;
        box-shadow: var(--shadow-md);
        z-index: 100;
        text-align: center;
      }

      .nav-links.active {
        display: flex;
      }

      .mobile-menu-btn {
        display: block;
      }

      .footer-content {
        flex-direction: column;
        gap: 1rem;
        text-align: center;
      }

      .footer-links {
        margin: 1rem 0;
      }

      .history-controls {
        flex-direction: column;
        align-items: stretch;
      }

      .filter-controls {
        justify-content: space-between;
      }
    }

    @media (max-width: 480px) {
      .navbar {
        padding: 1rem;
      }

      .nav-buttons {
        gap: 0.5rem;
      }

      .nav-btn {
        padding: 0.4rem 0.75rem;
        font-size: 0.8rem;
      }

      .page-header h1 {
        font-size: 1.8rem;
      }

      .page-header p {
        font-size: 1rem;
      }

      .history-list {
        grid-template-columns: 1fr;
      }
    }
  </style>
</head>
