<script>
  document.addEventListener('DOMContentLoaded', function() {
    // Mobile menu toggle
    const mobileMenuBtn = document.querySelector('.mobile-menu-btn');
    const navLinks = document.querySelector('.nav-links');

    mobileMenuBtn.addEventListener('click', function() {
      navLinks.classList.toggle('active');
    });

    // Search functionality
    const searchInput = document.querySelector('.search-box input');
    const historyCards = document.querySelectorAll('.history-card');

    searchInput.addEventListener('input', function() {
      const searchTerm = this.value.toLowerCase();

      historyCards.forEach(card => {
        const speaker = card.querySelector('.card-speaker').textContent.toLowerCase();
        const question = card.querySelector('.card-question').textContent.toLowerCase();

        if (speaker.includes(searchTerm) || question.includes(searchTerm)) {
          card.style.display = 'block';
        } else {
          card.style.display = 'none';
        }
      });
    });

    // Filter functionality
    const answerFilter = document.querySelectorAll('.filter-select')[0];

    answerFilter.addEventListener('change', function() {
      const filterValue = this.value;

      historyCards.forEach(card => {
        const answerType = card.querySelector('.card-answer').classList.contains('yes') ? 'yes' : 'no';

        if (filterValue === '' || filterValue === answerType) {
          card.style.display = 'block';
        } else {
          card.style.display = 'none';
        }
      });
    });

    // Delete functionality
    const deleteButtons = document.querySelectorAll('.card-btn[title="Delete"]');

    deleteButtons.forEach(button => {
      button.addEventListener('click', function() {
        const card = this.closest('.history-card');

        if (confirm('Are you sure you want to delete this history item?')) {
          card.style.opacity = '0';
          setTimeout(() => {
            card.remove();

            // Check if there are no more cards
            if (document.querySelectorAll('.history-card').length === 0) {
              showEmptyState();
            }
          }, 300);
        }
      });
    });

    // Share functionality
    const shareButtons = document.querySelectorAll('.card-btn[title="Share"]');

    shareButtons.forEach(button => {
      button.addEventListener('click', function() {
        const card = this.closest('.history-card');
        const speaker = card.querySelector('.card-speaker').textContent;
        const question = card.querySelector('.card-question').textContent;
        const answer = card.querySelector('.card-answer').textContent;

        alert(`Share functionality would share:\n${speaker}\n${question}\nAnswer: ${answer}`);
      });
    });

    // Function to show empty state
    function showEmptyState() {
      const historyList = document.querySelector('.history-list');
      const emptyState = document.createElement('div');
      emptyState.className = 'empty-state';
      emptyState.innerHTML = `
                    <svg class="empty-icon" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M12 8V12L15 15" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                        <path d="M3.05078 11.0002C3.27441 8.18983 4.51578 5.55733 6.53204 3.67879C8.54829 1.80025 11.1876 0.815969 13.9047 0.919535C16.6219 1.0231 19.1721 2.20601 21.0117 4.22C22.8513 6.23398 23.8228 8.94306 23.7119 11.7275C23.601 14.5119 22.4144 17.1408 20.4368 19.0801C18.4592 21.0193 15.8223 22.1125 13.0701 22.1351C10.318 22.1577 7.66359 21.1079 5.65407 19.2002C3.64455 17.2925 2.50492 14.6902 2.50078 12.0002" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                        <path d="M3.05078 16.0002H2.00078V11.0002H7.00078" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                    </svg>
                    <h3>No History Found</h3>
                    <p>You haven't asked the Oracle any questions yet, or all your history has been cleared.</p>
                    <a href="DecisionFront.html" class="empty-btn">Ask a Question</a>
                `;

      historyList.innerHTML = '';
      historyList.appendChild(emptyState);

      // Hide pagination
      document.querySelector('.pagination').style.display = 'none';
    }

    // Pagination functionality
    const pageButtons = document.querySelectorAll('.pagination .page-btn');

    pageButtons.forEach(button => {
      if (!button.classList.contains('disabled')) {
        button.addEventListener('click', function() {
          // Remove active class from all buttons
          pageButtons.forEach(btn => btn.classList.remove('active'));

          // Add active class to clicked button
          this.classList.add('active');

          // In a real app, this would load the next page of results
          // For this demo, we'll just scroll to the top
          window.scrollTo({
            top: document.querySelector('.page-header').offsetTop,
            behavior: 'smooth'
          });
        });
      }
    });
  });
</script>
</body>
</html>
