// src/app/register/register.component.ts
import { Component } from '@angular/core';
import { AuthService } from '../../Services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  name = '';
  email = '';
  password = '';
  confirmPassword = '';
  terms = false;
  errorMessage = '';

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  onSubmit() {
    this.errorMessage = '';

    if (this.password !== this.confirmPassword) {
      this.errorMessage = 'Passwords do not match';
      return;
    }
    if (!this.terms) {
      this.errorMessage = 'You must agree to the terms';
      return;
    }

    this.authService.register(this.email, this.password).subscribe({
      next: () => this.router.navigate(['/login']),
      error: err => {
        this.errorMessage = err.error || 'Registration failed';
      }
    });
  }

  togglePasswordVisibility(fieldId: string) {
    const input = document.getElementById(fieldId) as HTMLInputElement;
    input.type = input.type === 'password' ? 'text' : 'password';
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Register</title>
  <link rel="stylesheet" href="register.component.css">
</head>
<body>
<div class="container">
  <div class="form-container">
    <div class="form-header">
      <h1>Create an account</h1>
      <p>Please fill in the details to get started</p>
    </div>

    <form class="auth-form">
      <div class="form-group">
        <label for="name">Full Name</label>
        <input
          type="text"
          id="name"
          name="name"
          [(ngModel)]="name"
          placeholder="Enter your full name"
          required
        >      </div>

      <div class="form-group">
        <label for="email">Email</label>
        <input
          type="email"
          id="email"
          name="email"
          [(ngModel)]="email"
          placeholder="Enter your email"
          required
        >      </div>

      <div class="form-group">
        <label for="password">Password</label>
        <div class="password-input">
          <input
            type="password"
            id="password"
            name="password"
            [(ngModel)]="password"
            placeholder="Create a password"
            required
          >          <button type="button" class="toggle-password" (click)="togglePasswordVisibility('password')">
            <svg class="eye-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
              <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"></path>
              <circle cx="12" cy="12" r="3"></circle>
            </svg>
          </button>
        </div>
        <p class="password-hint">Must be at least 8 characters with 1 uppercase, 1 number, and 1 special character</p>
      </div>

      <div class="form-group">
        <label for="confirm-password">Confirm Password</label>
        <div class="password-input">
          <input
            type="password"
            id="confirm-password"
            name="confirmPassword"
            [(ngModel)]="confirmPassword"
            placeholder="Confirm your password"
            required
          >          <button type="button" class="toggle-password" onclick="togglePasswordVisibility('confirm-password')">
            <svg class="eye-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
              <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"></path>
              <circle cx="12" cy="12" r="3"></circle>
            </svg>
          </button>
        </div>
      </div>

      <div class="form-options">
        <div class="remember-me">
          <input
            type="checkbox"
            id="terms"
            name="terms"
            [(ngModel)]="terms"
            required
          >          <label for="terms">I agree to the <a href="#">Terms of Service</a> and <a href="#">Privacy Policy</a></label>
        </div>
      </div>

      <button type="submit" class="submit-btn">Create account</button>
    </form>

    <div class="form-footer">
      <p>Already have an account? <a href="../login/login.component.html">Sign in</a></p>
    </div>
  </div>
</div>

<script>
  function togglePasswordVisibility(inputId) {
    const passwordInput = document.getElementById(inputId);
    if (passwordInput.type === "password") {
      passwordInput.type = "text";
    } else {
      passwordInput.type = "password";
    }
  }
</script>
</body>
</html>
