import { Component } from '@angular/core';
// app.ts
async function sendArgument(): Promise<void> {
  const speakerInput = document.getElementById('speaker') as HTMLInputElement;
  const textInput    = document.getElementById('text')    as HTMLInputElement;
  const feedbackEl   = document.getElementById('feedback') as HTMLPreElement;

  const speaker = speakerInput.value.trim();
  const text    = textInput.value.trim();
  if (!speaker || !text) {
    alert('Both speaker and text are required');
    return;
  }

  try {
    const res = await fetch('/api/argument', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ speaker, text }),
    });

    if (!res.ok) {
      const err = await res.text();
      console.error('Error from /api/argument:', err);
      return;
    }

    const decision = await res.json();
    feedbackEl.textContent += JSON.stringify(decision, null, 2) + '\n\n';
  } catch (error) {
    console.error('Error sending argument:', error);
    alert('Failed to send argument. Please try again.');
  }
}

async function loadSummary(): Promise<void> {
  const summaryEl = document.getElementById('summary') as HTMLPreElement;

  try {
    const res = await fetch('/api/summary');

    if (!res.ok) {
      const err = await res.text();
      console.error('Error from /api/summary:', err);
      return;
    }

    const summary = await res.json();
    summaryEl.textContent = JSON.stringify(summary, null, 2);
  } catch (error) {
    console.error('Error loading summary:', error);
    alert('Failed to load summary. Please try again.');
  }
}

// wire up buttons once DOM is ready
document.addEventListener('DOMContentLoaded', () => {
  document.getElementById('sendBtn')?.addEventListener('click', sendArgument);
  document.getElementById('summaryBtn')?.addEventListener('click', loadSummary);
});

