import { Component } from '@angular/core';

@Component({
  selector: 'app-decisionmaking',
  imports: [],
  templateUrl: './decisionmaking.component.html',
  styleUrl: './decisionmaking.component.css'
})
export class DecisionmakingComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Real-Time Decisions</title>
  <link rel="stylesheet" href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap">
</head>
<body>
<!-- Header with Navigation -->
<header>
  <nav class="navbar">
    <a href="DecisionFront.html" class="logo">
      <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M21 12C21 16.9706 16.9706 21 12 21C7.02944 21 3 16.9706 3 12C3 7.02944 7.02944 3 12 3C16.9706 3 21 7.02944 21 12Z" stroke="currentColor" stroke-width="2"/>
        <path d="M12 8V16" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
        <path d="M8 12H16" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
      </svg>
      Oracle
    </a>
  </nav>
</header>

<!-- Main Content -->
<main class="main-content">
  <div class="container">
    <div>
      <h1 class="page-title">Real-Time Decisions</h1>
      <p class="page-subtitle">Get instant feedback on your arguments and decisions</p>
    </div>

    <!-- Decision Form -->
    <div class="form-container">
      <div class="form-header">
        <h2>Submit Your Argument</h2>
        <p>Enter a speaker and argument to receive feedback</p>
      </div>

      <form class="decision-form">
        <div class="form-group">
          <label for="speaker">Speaker</label>
          <input id="speaker" placeholder="Alice" required>
        </div>

        <div class="form-group">
          <label for="text">Argument</label>
          <input id="text" placeholder="Your argument here" required>
        </div>

        <button type="button" id="sendBtn" class="btn">Send</button>
      </form>
    </div>

    <!-- Feedback Results -->
    <div class="results-container">
      <div class="results-header">
        <h2 class="results-title">Feedback</h2>
      </div>
      <pre id="feedback" class="results-content"></pre>

      <div style="margin-top: 1.5rem;">
        <button id="summaryBtn" class="btn">Get Summary</button>
      </div>
    </div>

    <!-- Summary Results -->
    <div class="results-container">
      <div class="results-header">
        <h2 class="results-title">Summary</h2>
      </div>
      <pre id="summary" class="results-content"></pre>
    </div>
  </div>
</main>

<!-- Footer -->
<footer>
  <div class="footer-content">
    <div class="footer-logo">
      <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M21 12C21 16.9706 16.9706 21 12 21C7.02944 21 3 16.9706 3 12C3 7.02944 7.02944 3 12 3C16.9706 3 21 7.02944 21 12Z" stroke="currentColor" stroke-width="2"/>
        <path d="M12 8V16" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
        <path d="M8 12H16" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
      </svg>
      Oracle
    </div>

    <div class="footer-links">
      <a href="DecisionFront.html">Home</a>
      <a href="ask-oracle.html">Ask Oracle</a>
      <a href="history.html">History</a>
      <a href="real-time-decisions.html">Real-Time</a>
    </div>

    <div class="copyright">© 2025 Oracle. All rights reserved.</div>
  </div>
</footer>

<script>
  async function sendArgument() {
    const speaker = document.getElementById('speaker').value.trim();
    const text = document.getElementById('text').value.trim();
    if (!speaker || !text) return alert('Both speaker and text are required');

    try {
      const res = await fetch('/api/argument', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ speaker, text })
      });

      if (!res.ok) {
        const err = await res.text();
        console.error('Error from /api/argument:', err);
        return;
      }

      const decision = await res.json();
      document.getElementById('feedback').textContent +=
        JSON.stringify(decision, null, 2) + '\n\n';
    } catch (error) {
      console.error('Error sending argument:', error);
      alert('Failed to send argument. Please try again.');
    }
  }

  async function loadSummary() {
    try {
      const res = await fetch('/api/summary');

      if (!res.ok) {
        const err = await res.text();
        console.error('Error from /api/summary:', err);
        return;
      }

      const summary = await res.json();
      document.getElementById('summary').textContent =
        JSON.stringify(summary, null, 2);
    } catch (error) {
      console.error('Error loading summary:', error);
      alert('Failed to load summary. Please try again.');
    }
  }

  document.getElementById('sendBtn').addEventListener('click', sendArgument);
  document.getElementById('summaryBtn').addEventListener('click', loadSummary);
</script>
</body>
</html>
